(() => {
  // ns-hugo-imp:C:\Users\PC\AppData\Local\hugo_cache\modules\filecache\modules\pkg\mod\github.com\!hugo!blox\hugo-blox-builder\modules\blox-tailwind@v0.10.0\assets\js\hb-init.js
  function initTheme() {
    const root2 = document.documentElement;
    const defaultTheme2 = root2.dataset.wcThemeDefault;
    const setDark = () => {
      root2.classList.add("dark");
      root2.style.colorScheme = "dark";
    };
    const setLight = () => {
      root2.classList.remove("dark");
      root2.style.colorScheme = "light";
    };
    if ("wc-color-theme" in localStorage) {
      localStorage.getItem("wc-color-theme") === "dark" ? setDark() : setLight();
    } else {
      if (defaultTheme2 === "dark") setDark();
      else if (defaultTheme2 === "light") setLight();
      else if (defaultTheme2 === "system") {
        window.matchMedia("(prefers-color-scheme: dark)").matches ? setDark() : setLight();
      }
    }
  }
  function applyHugoStyleFixes() {
    document.addEventListener("DOMContentLoaded", () => {
      const checkboxes = document.querySelectorAll("li input[type='checkbox'][disabled]");
      checkboxes.forEach((e) => {
        const parent = e.parentElement?.parentElement;
        if (parent) parent.classList.add("task-list");
      });
      const liNodes = document.querySelectorAll(".task-list li");
      liNodes.forEach((nodes) => {
        const textNodes = Array.from(nodes.childNodes).filter((node) => node.nodeType === 3 && node.textContent && node.textContent.trim().length > 1);
        if (textNodes.length > 0) {
          const span = document.createElement("label");
          textNodes[0].after(span);
          const input = nodes.querySelector("input[type='checkbox']");
          if (input) span.appendChild(input);
          span.appendChild(textNodes[0]);
        }
      });
    });
  }

  // <stdin>
  var root = document.documentElement;
  var defaultTheme = root.dataset.wcThemeDefault || "system";
  window.hbb = {
    defaultTheme,
    setDarkTheme: () => {
      root.classList.add("dark");
      root.style.colorScheme = "dark";
    },
    setLightTheme: () => {
      root.classList.remove("dark");
      root.style.colorScheme = "light";
    }
  };
  initTheme();
  applyHugoStyleFixes();
})();
